// IMPORT ALL THE THINGS NEEDED FROM OTHER JAVASCRIPT SOURCE FILES
import { jsTPS_Transaction } from "./common/jsTPS.js"

// THIS TRANSACTION IS FOR ADDING A NEW ITEM TO A TODO LIST
export default class ChangeStateTransaction extends jsTPS_Transaction {
    constructor(app,oldS,newS) {
        super();
        this.app= app;
        this.oldState = oldS;
        this.newState = newS;
    }

    doTransaction() {
        this.app.setState(this.newState);
    }

    undoTransaction() {
        this.app.setState(this.oldState);
    }
}
