// IMPORT ALL THE THINGS NEEDED FROM OTHER JAVASCRIPT SOURCE FILES
import { jsTPS_Transaction } from "./common/jsTPS.js"

// THIS TRANSACTION IS FOR ADDING A NEW ITEM TO A TODO LIST
export default class ChangeStateTransaction extends jsTPS_Transaction {
    constructor(app,oldS,newS) {
        super();
        this.app= app;
        this.oldState = oldS;
        this.newState = newS;
    }

    doTransaction() {
        this.app.setState({
            toDoLists: this.oldState[0],
            currentList: this.oldState[1],
            nextListId: this.oldState[2],
            nextListItemId: this.oldState[3],
            useVerboseFeedback: this.oldState[4]
        },this.app.afterToDoListsChangeComplete);
    }

    undoTransaction() {
        this.app.setState({
            toDoLists: this.newState[0],
            currentList: this.newState[1],
            nextListId: this.newState[2],
            nextListItemId: this.newState[3],
            useVerboseFeedback: this.newState[4]
        },this.app.afterToDoListsChangeComplete);
    }
}
