import React, { Component } from 'react'

class TaskForm extends React.Component {
    constructor(props) {
      super(props);
      this.state = {value: this.props.task};
  
      this.handleChange = this.handleChange.bind(this);
    }
  
    handleChange(event) {
      this.props.changeTaskCallBack(this.props.itemChanged,this.state.value);
      this.setState({value: event.target.value});
    }
  
    render() {
      return (
            <input className="task-input" type="text" value={this.state.value} onChange={this.handleChange} />
      );
    }
  }
  export default TaskForm;
