import React, { Component } from 'react'

class StatusForm extends React.Component {
    constructor(props) {
      super(props);
      this.handleChange = this.handleChange.bind(this);
      this.state = {value: this.props.status};
    }
  
    handleChange(event) {
      this.props.changeStatusCallBack(this.props.itemChanged,event.target.value,this.state.value);
      this.setState({value: event.target.value});
    }


    render() {
      return (
            <select className="status-input" value={this.props.status} onChange={this.handleChange}>
                <option value="complete">Complete</option>
                <option value="incomplete">Incomplete</option>
            </select>
      );
    }
  }
  export default StatusForm;
